// Get current time and determine if we should execute morning routine
const now = new Date();
const hour = now.getHours();
const minute = now.getMinutes();
const dayOfWeek = now.getDay(); // 0 = Sunday, 1 = Monday, etc.

// Get user preferences from static data or database
const staticData = $getWorkflowStaticData('global');
const preferences = staticData.preferences || {
  weekdayWakeupTime: { hour: 6, minute: 30 },
  weekendWakeupTime: { hour: 8, minute: 0 },
  workDays: [1, 2, 3, 4, 5], // Monday through Friday
  gradualWakeup: true,
  gradualWakeupMinutes: 15,
  coffeeEnabled: true,
  weatherReport: true,
  trafficCheck: true
};

// Determine if today is a work day
const isWorkDay = preferences.workDays.includes(dayOfWeek);

// Get target wake up time based on day type
const wakeupTime = isWorkDay 
  ? preferences.weekdayWakeupTime 
  : preferences.weekendWakeupTime;

// Calculate pre-wakeup time for gradual wake-up sequence
let preWakeupTime = { ...wakeupTime };
if (preferences.gradualWakeup) {
  preWakeupTime.minute -= preferences.gradualWakeupMinutes;
  if (preWakeupTime.minute < 0) {
    preWakeupTime.hour -= 1;
    preWakeupTime.minute += 60;
  }
}

// Determine routine phase based on current time
let phase = 'none';
if (hour === preWakeupTime.hour && minute === preWakeupTime.minute) {
  phase = 'pre-wakeup';
} else if (hour === wakeupTime.hour && minute === wakeupTime.minute) {
  phase = 'wakeup';
} else if (hour === wakeupTime.hour && minute === wakeupTime.minute + 10) {
  phase = 'post-wakeup';
}

return {
  json: {
    phase,
    isWorkDay,
    wakeupTime,
    preWakeupTime,
    preferences,
    currentTime: { hour, minute },
    executeRoutine: phase !== 'none',
    timestamp: now.toISOString()
  }
};

// Prepare a personalized good morning routine
const isWorkDay = $input.item.json.isWorkDay;
const preferences = $input.item.json.preferences;

// Build a sequence of actions based on preferences
const actions = [];

// Open blinds
actions.push({
  domain: 'cover',
  service: 'set_cover_position',
  data: {
    entity_id: 'cover.bedroom_blinds',
    position: 100
  },
  description: 'Opening bedroom blinds'
});

// Turn on lights fully
actions.push({
  domain: 'light',
  service: 'turn_on',
  data: {
    entity_id: 'light.bedroom',
    brightness: 255,
    color_temp: 4500
  },
  description: 'Setting lights to energizing brightness'
});

// Start coffee if enabled
if (preferences.coffeeEnabled) {
  actions.push({
    domain: 'switch',
    service: 'turn_on',
    data: {
      entity_id: 'switch.coffee_maker'
    },
    description: 'Starting coffee maker'
  });
}

// Play morning music
actions.push({
  domain: 'media_player',
  service: 'play_media',
  data: {
    entity_id: 'media_player.bedroom_speaker',
    media_content_id: isWorkDay ? 'media-source://media_source/local/morning_energetic.mp3' : 'media-source://media_source/local/morning_relaxed.mp3',
    media_content_type: 'audio/mp3'
  },
  description: 'Playing morning music'
});

return {
  json: {
    ...$input.item.json,
    actions,
    morningMessage: isWorkDay 
      ? `Good morning! It's a work day. Your first meeting is at ${preferences.firstMeetingTime || '9:00 AM'}.` 
      : 'Good morning! Enjoy your day off.'
  }
};

// Process weather and traffic data to create a morning briefing
const weatherData = $node['Fetch Weather Data'].json;
const trafficData = $node['Fetch Traffic Data'].json;
const isWorkDay = $json.isWorkDay;

// Format weather information
const temperature = Math.round(weatherData.main.temp);
const weatherDescription = weatherData.weather[0].description;
const weatherMessage = `The current temperature is ${temperature}¬∞C with ${weatherDescription}.`;

// Format weather forecast
let forecastMessage = '';
if (weatherData.rain) {
  forecastMessage = ' Expect rain today, so take an umbrella!';
} else if (weatherData.snow) {
  forecastMessage = ' Expect snow today, so dress warmly!';
}

// Format commute information if it's a work day
let commuteMessage = '';
if (isWorkDay && trafficData.routes && trafficData.routes.length > 0) {
  const durationInTraffic = Math.round(trafficData.routes[0].legs[0].duration_in_traffic.value / 60);
  const normalDuration = Math.round(trafficData.routes[0].legs[0].duration.value / 60);

  if (durationInTraffic > normalDuration * 1.2) {
    commuteMessage = ` Your commute currently takes ${durationInTraffic} minutes due to heavy traffic. Consider leaving earlier.`;
  } else {
    commuteMessage = ` Your commute currently takes ${durationInTraffic} minutes with normal traffic.`;
  }
}

// Combine all information into a morning briefing
const briefing = `${weatherMessage}${forecastMessage}${commuteMessage}`;

return {
  json: {
    ...$json,
    briefing,
    temperature,
    weatherCondition: weatherData.weather[0].main,
    weatherDescription,
    commuteTimeMinutes: isWorkDay && trafficData.routes ? Math.round(trafficData.routes[0].legs[0].duration_in_traffic.value / 60) : 0
  }
};

// Prepare personalized morning information display
