  'Rain': 'üåßÔ∏è',
  'Drizzle': 'üå¶Ô∏è',
  'Thunderstorm': '‚õàÔ∏è',
  'Snow': '‚ùÑÔ∏è',
  'Mist': 'üå´Ô∏è',
  'Fog': 'üå´Ô∏è',
  'Haze': 'üå´Ô∏è'
}[$json.weatherCondition] || 'üå°Ô∏è';

const cardData = {
  type: 'custom:stack-in-card',
  cards: [
    {
      type: 'markdown',
      content: `# Good Morning, ${name}! ${weatherEmoji}\n\n**${$json.temperature}¬∞C** ¬∑ ${$json.weatherDescription}\n\n${$json.isWorkDay ? `üöó **Commute**: ${$json.commuteTimeMinutes} mins` : 'üè† Enjoy your day off!'}`
    }
  ],
  style: {
    border_radius: '12px',
    box_shadow: '3px 3px 10px rgba(0,0,0,0.2)',
    padding: '12px'
  }
};

